import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";

import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import React, { Component } from 'react';
import './assets/style.css';
import axios from 'axios';

class Register extends Component {

  constructor(){
    super();
    this.state = {
      username: '',
      email: '',
      password: '',
      currency:''

      };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleChange(e){
    let target = e.target;
    let value = target.value;
    let name = target.name;
    this.setState({
      [name]: value
    });
  }
  handleSubmit(e){

    // make API call
    e.preventDefault()
    axios.post('http://localhost:6200/users/register',this.state)
    .then(res =>{
        axios.post('http://localhost:6200/users/login', {email:this.state.email, password:this.state.password})
        .then(response =>{
          localStorage.setItem("auth-token", response.data.token);
          window.location.replace('/ChoixCrypto');
        }).catch(err => {
          alert(err)
        })
    }).catch(error =>{
      alert(error)
    })
  }

  insertData(nonce){
    axios.get('http://wp.ruvictor.com/api/user/register/?username='+this.state.username+'&email='+this.state.email+'&password='+this.state.password)
    .then(res => {
    }).catch(error => {
      console.log(error.response)
  });
  }

  getWPnonce(){
    axios.get('http://wp.ruvictor.com/api/get_nonce/?controller=user&method=register')
    .then(res => {
      this.insertData(res.data.nonce);
    }).catch(error => {
      console.log(error.response)
  });
  }

  render(){
    console.log(this.state)
    return (
      <div className="wrapper">
          <div className="form-wrapper">



            <MDBContainer>
              <MDBRow>
                <MDBCol md="6">
                  <form onSubmit={this.handleSubmit}>
                    <p className="h5 text-center mb-4">Sign up</p>
                    <div className="grey-text">
                      <MDBInput label="Name"  name="username" value={this.state.username} onChange={this.handleChange} htmlFor="exampleInputEmail1" icon="user" group type="text" validate error="wrong"
                                success="right" style={{width: '180%'}} />

                      <MDBInput label="Email" icon="envelope" name="email" value={this.state.email} onChange={this.handleChange} group type="email" validate error="wrong"
                                success="right" style={{width: '180%'}} />

                      <MDBInput label="Password" name="password" icon="lock" group type="password" style={{width: '180%'}}  validate />s

                      <MDBInput label="Confirm your password " name ="display_name2" onChange={this.handleConfirmPassword} icon="lock" group type="password" style={{width: '200%'}}  validate/>

                      <MDBInput label="Enter Devise" name="currency" style={{width: '180%'}}  value={this.state.currency} onChange={this.handleChange} group type="text" validate error="wrong"
                                success="right" />
                    </div>
                    <div className="text-center">
                      <MDBBtn type="submit" color="primary">Register</MDBBtn>
                    </div>
                  </form>
                </MDBCol>
              </MDBRow>
            </MDBContainer>

          </div>
      </div>


    );
  }
}
export default Register;
