import React, { Component } from "react";
import "./assets/style.css";
import axios from 'axios';
import {Table } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

let arr=[]
class ChoixCrypto extends Component {

    constructor(props){
       super(props);
       this.state = {
           posts:[]
       },

       this.add = this.add.bind(this);
    }
    componentDidMount(){
       axios.get('http://localhost:6200/cryptos/liste')
       .then(response =>{
           console.log(response)
           this.setState({posts: response.data})
       })
       .catch(error=>{
       console.log(error)
       })
       }

    add(id){
        arr.push(id);
        console.log(arr)

    }
    submit(array){
        //axios.put('http://localhost:6200/cryptos/'+array,{headers : {"auth-token": localStorage.getItem("auth-token")}})
        alert("ajouté")
        //window.location.replace('/PrinOn');
    }

       render(){
       const { posts}=this.state
       return(
           <div className="wrapper">
               <div className="f-wrapper"> 

                    <p></p>
                    <h1>List of Cryptocurrencies </h1>
                    <p></p>
                    <p></p>

                <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Symbol</th>
                            </tr>
                        </thead>
                        <tbody>
                        {
                        posts.length ?

                            posts.map(post=> <tr key={post.symbole}>
                            <td>{post.nom}</td>
                            <td>{post.symbole}</td>
                            <td><Button onClick={this.add.bind(this, post.symbole)} variant="secondary">Add</Button> </td>
                            </tr>):
                            null
                            }   
                        </tbody>
                    </Table>

                    <Button  onSubmit={this.submit}  variant="secondary">Submit</Button>
                </div>
           </div>

       )
       }}
       export default ChoixCrypto
