import React, { Component } from "react";
import "./assets/style.css";
import axios from 'axios';



class Profil extends Component {

  constructor(props){
    super(props);
    this.state = {
      username: '',
      email: '',
      password: '',
      currency: '',
      keywords: '',
      cryptocurrencies: ''
    }
  }

  changeColor(){
    axios.get('http://localhost:6200/users/profile',{headers : {"auth-token": localStorage.getItem("auth-token")}})
    .then( response => {
      this.setState = {
        username: response.data.user.username,
        email: response.data.user.email,
        password: '',
        currency: response.data.user.currency,
        keywords: response.data.user.keywords,
        cryptocurrencies: response.data.user.cryptocurrencies
      };
      console.log(this.state)
    }).catch(err =>{
      alert(err);
    })

  }

 render(){
    return (
      <div>
        <h1>My {this.state.username}</h1>
        <p>
          It is a {this.state.email}
          {this.state.password}
          from {this.state.currency}
          and {this.state.keywords}
          and {this.state.cryptocurrencies}
        </p>
        <button
          type="button"
          onClick={this.changeColor}
        >Change color</button>
      </div>
    );
  }
}

export default Profil;


