import React, { Component } from "react";
import "./assets/style.css";
import axios from 'axios';
import {Table } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

let arr=[];
let emailU = '';
let passwordU ='';

class ChoixCrypto extends Component {

    constructor(props){
       super(props);
       this.state = {
           posts:[]
       };

       this.add = this.add.bind(this);
    }
    state ={
        posts:[]
    }

    componentDidMount(){
       axios.get('http://localhost:6200/cryptos/liste')
       .then(response =>{
           console.log(response)
           this.setState({posts: response.data})
       })
       .catch(error=>{
        alert(error)
       })

       axios.get('http://localhost:6200/users/profile', {headers : {"auth-token": localStorage.getItem("auth-token")}})
        .then(response => {
            emailU = response.data.user.email;
            passwordU = response.data.user.password;            
        })
        .catch(error=>{
         alert(error)
        })

    }
    add(id){
        if ( !arr.includes(id)){
        arr.push(id);
        }
        console.log(arr)

    }
    submit(event){
        event.preventDefault()
        console.log(arr)
        axios.put('http://localhost:6200/users/profile', {cryptoCurrencies:arr} ,{headers : {"auth-token": localStorage.getItem("auth-token")}})
        .then(response => {
            alert("Ajouté");
            axios.post('http://localhost:6200/users/relogin', {email: emailU, password: passwordU})
            .then(response =>{
                localStorage.setItem("auth-token", response.data.token);
                window.location.replace('/PrinOn');
                console.log(emailU);
                console.log(passwordU);

            }).catch(err => {
                alert(err)
            })
        }).catch(err => {
            alert(err)
        })
    }
       render(){
       const { posts}=this.state
       return(
           <div className="wrapper">
               <div className="f-wrapper"> 
               <form onSubmit={this.submit}>
                    <p></p>
                    <h1>List of Cryptocurrencies </h1>
                    <p></p>
                    <p></p>

                <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Symbol</th>
                            </tr>
                        </thead>
                        <tbody>
                        {
                        posts.length ?

                            posts.map(post=> <tr key={post.symbole}>
                            <td>{post.nom}</td>
                            <td>{post.symbole}</td>
                            <td><Button onClick={this.add.bind(this, post.symbole)} variant="secondary">Add</Button> </td>
                            </tr>):
                            null
                            }   
                        </tbody>
                    </Table>

                    <div className="createAccount">
            <button type="submit"  className="btn btn-primary" >Submit</button>
          </div>
                    </form>
                </div>
           </div>

       )
       }}
       export default ChoixCrypto
