import React, { Component } from "react";
import "./assets/style.css";
import axios from 'axios';
import {Table } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

class ChoixCrypto extends Component {

    constructor(props){
       super(props);
       this.state = {
           posts:[],
           arr:[],
       };

       this.add = this.add.bind(this);
    }
    state ={
        posts:[],
        arr:[],
    }
    componentDidMount(){
       axios.get('http://localhost:6200/cryptos/liste')
       .then(response =>{
           console.log(response)
           this.setState({posts: response.data})
       })
       .catch(error=>{
       console.log(error)
       })
       }

    add(id){
        if ( !this.state.arr.includes(id)){
        this.state.arr.push(id);
        }
        console.log(this.state.arr)

    }
    submit(event){
        event.preventDefault()
        axios.put('http://localhost:6200/users/profile'+ {cryptoCurrencies:this.state.arr} ,{headers : {"auth-token": localStorage.getItem("auth-token")}})
        .then(response => {
            alert("ajouté");
        }).catch(err => {
            alert(err)
        })
        //window.location.replace('/PrinOn');
    }
       render(){
       const { posts}=this.state
       return(
           <div className="wrapper">
               <div className="f-wrapper"> 
               <form onSubmit={this.submit}>
                    <p></p>
                    <h1>List of Cryptocurrencies </h1>
                    <p></p>
                    <p></p>

                <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Symbol</th>
                            </tr>
                        </thead>
                        <tbody>
                        {
                        posts.length ?

                            posts.map(post=> <tr key={post.symbole}>
                            <td>{post.nom}</td>
                            <td>{post.symbole}</td>
                            <td><Button onClick={this.add.bind(this, post.symbole)} variant="secondary">Add</Button> </td>
                            </tr>):
                            null
                            }   
                        </tbody>
                    </Table>

                    <div className="createAccount">
            <button type="submit"  className="btn btn-primary" >Submit</button>
          </div>
                    </form>
                </div>
           </div>

       )
       }}
       export default ChoixCrypto
