import React, { Component } from "react";
import "./assets/style.css";
import axios from 'axios';
import {Table } from 'react-bootstrap';
import Nav from 'react-bootstrap/Nav';
import Button from 'react-bootstrap/Button';

class Admin extends Component {

    constructor(props){
       super(props);
       this.state = {
           posts:[]
       }
    }
    componentDidMount(){
       axios.get('http://localhost:6200/cryptos')
       .then(response =>{
           console.log(response)
           this.setState({posts: response.data})
       })
       .catch(error=>{
       console.log(error)
       })
       }
       OnDeleteClick(event){
        event.preventDefault()
        console.log(this.state)
           axios.delete(
               'http://localhost:6200/cryptos/',
               { data: this.state.id },
               {
                   headers: {
                       "Authorization": localStorage.getItem("access_token"),
                       "Content-Type": "application/json"
                   }
               }
           ).then(data => {
               console.log(data)

           }).catch(err => {
              console.log(err)
        })

       }
       render(){
       const { posts}=this.state
       const divStyle = {
        height :'100px',

      }
       return(
           <div className="wrapper">
               <div className="f-wrapper"> 
                <Nav className="justify-content-end" activeKey="/">
                        <Nav.Item>
                        <Nav.Link href="/PrincipalOff2">Principal</Nav.Link>
                        </Nav.Item>

                        <Nav.Item>
                        <Nav.Link href="/Register">Logout</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>

                        </Nav.Item>
                    </Nav>
                    <p></p>
                    <h1>List of Cryptocurrencies </h1>
                    <p></p>
                    <p></p>

                <Table striped bordered hover size="sm">
                        <thead>
                            <tr>

                            <th>Name</th>
                            <th>Current Price </th>
                            <th>Opening Price</th>
                            <th>Lowest Price of the day</th>
                            <th>Highest Price of the day</th>
                            <th>Image</th>
                            <th> </th>
                            </tr>
                        </thead>
                        <tbody>
                        {
                        posts.length ?

                            posts.map(post=> <tr key={post.id}>

                            <td>{post.Cryptommonaie}</td>
                            <td>{post.Prix}</td>
                            <td>{post["Prix Ã  l'Ouverture"]} </td>
                            <td>{post["Prix le plus bas"]}</td>
                            <td>{post["Prix le plus Haut"]}</td>
                            <td><img src={post.URL}alt="..." style={ divStyle}/></td>
                            <td><Button onClick={this.OnDeleteClick} variant="secondary">Delete</Button> </td>
                            </tr>):
                            null
                            }


                        </tbody>
                    </Table>
                </div>
           </div>

       )
       }}
       export default Admin
